// src/supabase.ts
import { createClient } from '@supabase/supabase-js';

console.log('üîÑ Carregando configura√ß√£o do Supabase...');
console.log('Variables encontradas:', {
  REACT_APP_SUPABASE_URL: process.env.REACT_APP_SUPABASE_URL ? 'DEFINIDA' : 'UNDEFINED',
  REACT_APP_SUPABASE_ANON_KEY: process.env.REACT_APP_SUPABASE_ANON_KEY ? 'DEFINIDA' : 'UNDEFINED'
});

const supabaseUrl = process.env.REACT_APP_SUPABASE_URL;
const supabaseAnonKey = process.env.REACT_APP_SUPABASE_ANON_KEY;

// Verificar se as vari√°veis de ambiente est√£o definidas
if (!supabaseUrl || !supabaseAnonKey) {
  console.error('‚ùå ERRO: Vari√°veis de ambiente do Supabase n√£o configuradas');
  console.error('REACT_APP_SUPABASE_URL:', supabaseUrl);
  console.error('REACT_APP_SUPABASE_ANON_KEY:', supabaseAnonKey ? 'Definida' : 'N√£o definida');
  console.error('üìã Verificar arquivo .env na raiz do projeto');
  
  // Em vez de crash, criar um cliente b√°sico com fallback
  throw new Error('‚ùå SUPABASE N√ÉO CONFIGURADO: Verifique arquivo .env');
}

console.log('‚úÖ Supabase configurado:', supabaseUrl);

export const supabase = createClient(supabaseUrl, supabaseAnonKey, {
  auth: {
    autoRefreshToken: true,
    persistSession: true,
    detectSessionInUrl: true
  }
});

// Fun√ß√£o para criar documento do usu√°rio automaticamente
async function createUserDocumentFromAuth(user: any) {
  console.log('üîé [createUserDocumentFromAuth] Usu√°rio recebido:', user);
  // Tenta encontrar o campo correto para o id
  const userId = user?.id || user?.uuid || user?.user?.id;
  if (!userId) {
    console.error('‚ùå createUserDocumentFromAuth: Nenhum campo de id encontrado no objeto user:', user);
    return;
  }

  try {
    // Buscar se o usu√°rio j√° existe
    const { data: existingUser, error: fetchError } = await supabase
      .from('users')
      .select('uuid')
      .eq('uuid', userId)
      .single();

    if (fetchError && fetchError.code !== 'PGRST116') { // PGRST116 = not found
      console.error('‚ùå Erro ao verificar usu√°rio existente:', fetchError);
      return;
    }

    if (existingUser) {
      console.log('‚ÑπÔ∏è Usu√°rio j√° existe no banco:', user.email);
      return;
    }

    // Criar novo documento do usu√°rio
    const userData = {
  id: userId, // Chave prim√°ria
  uuid: userId, // Compatibilidade
      email: user.email,
      display_name: user.user_metadata?.full_name || user.user_metadata?.display_name || user.email?.split('@')[0] || 'Usu√°rio',
      username: user.user_metadata?.username || user.user_metadata?.full_name || user.user_metadata?.display_name || user.email?.split('@')[0] || 'Usu√°rio',
      avatar_url: user.user_metadata?.avatar_url || null,
      created_at: new Date().toISOString(),
      updated_at: new Date().toISOString(),
    };

    const { error: insertError } = await supabase
      .from('users')
      .insert(userData);

    if (insertError) {
      console.error('‚ùå Erro ao criar documento do usu√°rio:', insertError);
    } else {
      console.log('‚úÖ Documento do usu√°rio criado:', userData.email);
    }

  } catch (error) {
    console.error('‚ùå Erro inesperado ao criar documento do usu√°rio:', error);
  }
}

// Sistema de compatibilidade simples para auth
export const auth = {
  currentUser: null as any,
  _callbacks: [] as any[],
  
  onAuthStateChanged(callback: (user: any) => void) {
    this._callbacks.push(callback);
    
    // Listener real do Supabase
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        console.log('üë§ Auth state changed:', event, session?.user?.email || 'sem usu√°rio');
        
        if (session?.user) {
          this.currentUser = session.user;
          
          // Criar documento do usu√°rio se necess√°rio
          await createUserDocumentFromAuth(session.user);
        } else {
          this.currentUser = null;
        }
        
        // Notificar todos os callbacks
        this._callbacks.forEach(cb => cb(this.currentUser));
      }
    );
    
    // Retornar fun√ß√£o de cleanup
    return () => {
      subscription?.unsubscribe();
      const index = this._callbacks.indexOf(callback);
      if (index > -1) {
        this._callbacks.splice(index, 1);
      }
    };
  }
};

// Verificar sess√£o inicial
supabase.auth.getSession().then(({ data: { session }, error }) => {
  if (error) {
    console.error('‚ùå Erro ao verificar sess√£o inicial:', error);
    auth.currentUser = null;
    auth._callbacks.forEach(cb => cb(null));
    return;
  }
  
  if (session?.user) {
    console.log('‚úÖ Sess√£o ativa encontrada:', session.user.email);
    auth.currentUser = session.user;
    
    // Criar documento do usu√°rio se necess√°rio
    createUserDocumentFromAuth(session.user).catch(error => {
      console.error('‚ùå Erro ao criar documento na sess√£o inicial:', error);
    });
    
    // Notificar todos os callbacks sobre o usu√°rio logado
    auth._callbacks.forEach(cb => cb(auth.currentUser));
  } else {
    console.log('‚ÑπÔ∏è Nenhuma sess√£o ativa encontrada - usu√°rio deve fazer login');
    auth.currentUser = null;
    auth._callbacks.forEach(cb => cb(null));
  }
}).catch((error) => {
  console.error('‚ùå Erro ao verificar sess√£o inicial:', error);
  auth.currentUser = null;
  auth._callbacks.forEach(cb => cb(null));
});

// Provider do Google (compatibilidade)
export const googleProvider = {
  providerId: 'google.com'
};

// Interface compat√≠vel com Firestore usando Supabase
export const db = {
  // Ser√° implementado no adaptador se necess√°rio
};

export const supabaseApi = {
  getUser: () => auth.currentUser,
  getSupabaseUser: () => supabase.auth.getUser(),
  supabase: supabase
};
