// ÔøΩÔ∏è Remove tradu√ß√£o espec√≠fica do cache
export async function removeTranslation(cardId, cardName) {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readwrite");
    const store = tx.objectStore(STORE_NAME);
    const key = `${cardId}_${cardName}`;
    await store.delete(key);
    console.log(`üóëÔ∏è Tradu√ß√£o removida do cache: ${cardName}`);
    return true;
  } catch (error) {
    console.error("‚ùå Erro ao remover tradu√ß√£o:", error);
    return false;
  }
}
// ÔøΩüó£Ô∏è Cache de tradu√ß√µes no IndexedDB
// Evita chamadas repetidas √† Azure Translator API

const DB_NAME = "deckmaster_translations";
const DB_VERSION = 1;
const STORE_NAME = "translations";

// üìä Estat√≠sticas de cache
let stats = {
  hits: 0,
  misses: 0,
  saves: 0
};

// Inicializa o IndexedDB
function openDB() {
  return new Promise((resolve, reject) => {
    const request = indexedDB.open(DB_NAME, DB_VERSION);

    request.onerror = () => reject(request.error);
    request.onsuccess = () => resolve(request.result);

    request.onupgradeneeded = (event) => {
      const db = event.target.result;
      
      // Cria store se n√£o existir
      if (!db.objectStoreNames.contains(STORE_NAME)) {
        const store = db.createObjectStore(STORE_NAME, { keyPath: "key" });
        
        // √çndices para busca
        store.createIndex("cardId", "cardId", { unique: false });
        store.createIndex("timestamp", "timestamp", { unique: false });
        
        console.log("‚úÖ Store de tradu√ß√µes criado");
      }
    };
  });
}

// üíæ Salva tradu√ß√£o no cache
export async function saveTranslation(cardId, cardName, translatedName, translatedText, faces) {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readwrite");
    const store = tx.objectStore(STORE_NAME);

    const cacheEntry = {
      key: `${cardId}_${cardName}`, // Chave √∫nica
      cardId,
      cardName,
      translatedName,
      translatedText,
      faces: faces || null, // Array de faces traduzidas (se houver)
      timestamp: Date.now(),
    };

    await store.put(cacheEntry);
    stats.saves++;
    
    console.log(`üíæ Tradu√ß√£o salva: ${cardName} ‚Üí ${translatedName}`);
    return true;
  } catch (error) {
    console.error("‚ùå Erro ao salvar tradu√ß√£o:", error);
    return false;
  }
}

// üîç Busca tradu√ß√£o no cache
export async function getTranslation(cardId, cardName) {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readonly");
    const store = tx.objectStore(STORE_NAME);
    
    const key = `${cardId}_${cardName}`;
    const result = await new Promise((resolve, reject) => {
      const request = store.get(key);
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });

    if (result) {
      stats.hits++;
      console.log(`‚úÖ Cache HIT: ${cardName}`);
      return result;
    }

    stats.misses++;
    console.log(`‚ùå Cache MISS: ${cardName}`);
    return null;
  } catch (error) {
    console.error("‚ùå Erro ao buscar tradu√ß√£o:", error);
    stats.misses++;
    return null;
  }
}

// üóëÔ∏è Remove tradu√ß√µes antigas (mais de 30 dias)
export async function cleanOldTranslations() {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readwrite");
    const store = tx.objectStore(STORE_NAME);
    const index = store.index("timestamp");

    const thirtyDaysAgo = Date.now() - (30 * 24 * 60 * 60 * 1000);
    const range = IDBKeyRange.upperBound(thirtyDaysAgo);

    let deleted = 0;
    const cursorRequest = index.openCursor(range);

    return new Promise((resolve, reject) => {
      cursorRequest.onsuccess = (event) => {
        const cursor = event.target.result;
        if (cursor) {
          cursor.delete();
          deleted++;
          cursor.continue();
        } else {
          console.log(`üßπ ${deleted} tradu√ß√µes antigas removidas`);
          resolve(deleted);
        }
      };
      cursorRequest.onerror = () => reject(cursorRequest.error);
    });
  } catch (error) {
    console.error("‚ùå Erro ao limpar tradu√ß√µes:", error);
    return 0;
  }
}

// üìä Obt√©m estat√≠sticas do cache
export function getStats() {
  return {
    ...stats,
    hitRate: stats.hits + stats.misses > 0 
      ? ((stats.hits / (stats.hits + stats.misses)) * 100).toFixed(2) + '%'
      : '0%'
  };
}

// üîÑ Reseta estat√≠sticas
export function resetStats() {
  stats = { hits: 0, misses: 0, saves: 0 };
}

// üóëÔ∏è Limpa todo o cache (cuidado!)
export async function clearAllTranslations() {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readwrite");
    const store = tx.objectStore(STORE_NAME);
    
    await store.clear();
    console.log("üóëÔ∏è Todas as tradu√ß√µes foram removidas");
    return true;
  } catch (error) {
    console.error("‚ùå Erro ao limpar cache:", error);
    return false;
  }
}

// üìà Conta total de tradu√ß√µes no cache
export async function countTranslations() {
  try {
    const db = await openDB();
    const tx = db.transaction(STORE_NAME, "readonly");
    const store = tx.objectStore(STORE_NAME);
    
    const count = await new Promise((resolve, reject) => {
      const request = store.count();
      request.onsuccess = () => resolve(request.result);
      request.onerror = () => reject(request.error);
    });

    return count;
  } catch (error) {
    console.error("‚ùå Erro ao contar tradu√ß√µes:", error);
    return 0;
  }
}

// üåç Exp√µe no window para debug
if (typeof window !== 'undefined') {
  window.translationCache = {
    getStats,
    resetStats,
    clearAll: clearAllTranslations,
    count: countTranslations,
    clean: cleanOldTranslations,
  };
  console.log("üó£Ô∏è Translation Cache dispon√≠vel em window.translationCache");
}

export default {
  saveTranslation,
  getTranslation,
  cleanOldTranslations,
  getStats,
  resetStats,
  clearAllTranslations,
  countTranslations,
};
